function hideDiv(b) {
    var c = b.parent("div.media_section");
    var a = c.find("input[data-name=useMeDelete]").val();
    c.find("input[data-name=deleteMe]").val(a);
    c.fadeOut("slow")
}
function readURL(d) {
    if (d.files && d.files[0]) {
        var b, c, a;
        for (var e = 0; e < d.files.length; e++) {
            b = new FileReader();
            b.onload = function (f) {
                c = $("<img >");
                c.attr("src", f.target.result);
                a = $(".media_browser .media_section:last-child label");
                a.css("border", "none");
                a.html("");
                c.appendTo(a);
                addImages()
            };
            b.readAsDataURL(d.files[e])
        }
    }
}
function removeImages() {
    var a = $(".media_section");
    a.each(function () {
        var b = $(this).find("input[data-name=useMeDelete]").val();
        $(this).find("input[data-name=deleteMe]").val(b);
        $(this).fadeOut("slow")
    })
};